class BankAccount:
    def __init__(self, account_number):
        """Initialize the bank account with an account number and a balance of 0."""
        self.account_number = account_number
        self.balance = 0

    def deposit(self, amount):
        """Add a specified amount to the account balance."""
        if amount > 0:
            self.balance += amount
            print(f"Deposited ${amount}. New balance: ${self.balance}.")
        else:
            print("Deposit amount must be positive.")

    def withdraw(self, amount):
        """Subtract a specified amount from the account balance, ensuring the balance does not go below 0."""
        if amount > 0:
            if amount <= self.balance:
                self.balance -= amount
                print(f"Withdrew ${amount}. New balance: ${self.balance}.")
            else:
                print("Insufficient funds.")
        else:
            print("Withdrawal amount must be positive.")

    def get_balance(self):
        """Return the current balance of the account."""
        return self.balance

