from datetime import datetime, timedelta

def calculate_meeting_duration(start_time: str, end_time: str) -> int:
    """
    Calculate the duration of a meeting given its start and end times in 'HH:MM' format.

    :param start_time: A string representing the start time in 'HH:MM' format.
    :param end_time: A string representing the end time in 'HH:MM' format.
    :return: The duration of the meeting in minutes.
    """
    time_format = "%H:%M"

    start = datetime.strptime(start_time, time_format)
    end = datetime.strptime(end_time, time_format)

    if end < start:
        end += timedelta(days=1)

    duration = end - start
    return int(duration.total_seconds() / 60)

def main():
    start_time = input("Enter the start time of the stand-up in HH:MM format: ")
    end_time = input("Enter the end time of the stand-up in HH:MM format: ")

    try:
        duration_minutes = calculate_meeting_duration(start_time, end_time)
        print(f"The duration of the stand-up was {duration_minutes} minutes.")
    except ValueError:
        print("Invalid time format! Please enter times in HH:MM format.")

if __name__ == "__main__":
    main()

def print_board(board):
    """Display the Tic-Tac-Toe board."""
    for row in board:
        print(" | ".join(row))
        print("-" * 5)

def check_winner(board, player):
    """Check if the player has won."""
    # Check rows and columns for a win
    for i in range(3):
        if all([cell == player for cell in board[i]]) or all([board[j][i] == player for j in range(3)]):
            return True
    # Check diagonals for a win
    if all([board[i][i] == player for i in range(3)]) or all([board[i][2 - i] == player for i in range(3)]):
        return True
    return False

def is_full(board):
    """Check if the board is full."""
    return all([cell != ' ' for row in board for cell in row])

def make_move(board, player, row, col):
    """Place player's marker on the board."""
    if board[row][col] != ' ':
        return False
    board[row][col] = player
    return True

def tic_tac_toe():
    """Simulate a Tic-Tac-Toe game."""
    board = [[' ' for _ in range(3)] for _ in range(3)]
    current_player = 'X'
    while True:
        print_board(board)
        print(f"Player {current_player}'s turn")
        row = int(input("Enter the row (0, 1, or 2): "))
        col = int(input("Enter the column (0, 1, or 2): "))

        if not make_move(board, current_player, row, col):
            print("Invalid move! Try again.")
            continue

        if check_winner(board, current_player):
            print_board(board)
            print(f"Player {current_player} wins!")
            break

        if is_full(board):
            print_board(board)
            print("It's a tie!")
            break

        current_player = 'O' if current_player == 'X' else 'X'

if __name__ == '__main__':
    tic_tac_toe()

import random

class Stock:
    def __init__(self, symbol, price):
        self.symbol = symbol
        self.price = price

    def simulate_price_change(self):
        """Simulate a 5% random price change for the stock."""
        change = self.price * random.uniform(-0.05, 0.05)
        self.price += change
        self.price = max(0.01, self.price)  # Ensure the stock price stays positive

class Portfolio:
    def __init__(self):
        self.stocks = {}

    def add_stock(self, symbol, amount, price):
        """Add stocks to the portfolio."""
        if symbol in self.stocks:
            self.stocks[symbol]['amount'] += amount
        else:
            self.stocks[symbol] = {'amount': amount, 'price': price}

    def display_portfolio(self):
        """Display the current portfolio."""
        print("Portfolio:")
        for symbol, info in self.stocks.items():
            print(f"Stock {symbol}: {info['amount']} shares at {info['price']:.2f} per share")
        print()

    def update_stock_prices(self, stocks_dict):
        """Update stock prices in the portfolio based on the given `stocks_dict`."""
        for symbol, stock in stocks_dict.items():
            if symbol in self.stocks:
                self.stocks[symbol]['price'] = stock.price

    def buy(self, stocks_dict, symbol, amount):
        """Buy stocks from the market."""
        if symbol in stocks_dict:
            stock = stocks_dict[symbol]
            cost = stock.price * amount
            print(f"Buying {amount} shares of {symbol} at {stock.price:.2f} per share for a total of {cost:.2f}")
            self.add_stock(symbol, amount, stock.price)
        else:
            print(f"Stock {symbol} not found in market")

    def sell(self, symbol, amount):
        """Sell stocks in the portfolio."""
        if symbol in self.stocks and self.stocks[symbol]['amount'] >= amount:
            price = self.stocks[symbol]['price']
            proceeds = price * amount
            print(f"Selling {amount} shares of {symbol} at {price:.2f} per share for a total of {proceeds:.2f}")
            self.stocks[symbol]['amount'] -= amount
            if self.stocks[symbol]['amount'] == 0:
                del self.stocks[symbol]
        else:
            print(f"Not enough shares of {symbol} in portfolio to sell")

def simulate_day(stocks_dict, portfolio):
    """Simulate one day in the market."""
    print("Simulating a day in the market...")
    for stock in stocks_dict.values():
        stock.simulate_price_change()
        print(f"Stock {stock.symbol} new price: {stock.price:.2f}")
    portfolio.update_stock_prices(stocks_dict)
    portfolio.display_portfolio()

