def get_dimensions():
    """Prompts the user to input the dimensions of a rectangle and returns them as a tuple."""
    length = float(input("Enter the length of the rectangle: "))
    width = float(input("Enter the width of the rectangle: "))
    return length, width

def calculate_area(length, width):
    """Calculates and returns the area of a rectangle given its length and width."""
    return length * width

def calculate_perimeter(length, width):
    """Calculates and returns the perimeter of a rectangle given its length and width."""
    return 2 * (length + width)

def display_results(area, perimeter):
    """Displays the area and perimeter of the rectangle."""
    print(f"The area of the rectangle is: {area}")
    print(f"The perimeter of the rectangle is: {perimeter}")

def main():
    length, width = get_dimensions()
    area = calculate_area(length, width)
    perimeter = calculate_perimeter(length, width)
    display_results(area, perimeter)

