    main()

import random

def initialize_board(size):
    """Initialize the board with size x size empty cells."""
    return [["." for _ in range(size)] for _ in range(size)]

def display_board(board):
    """Prints the board."""
    for row in board:
        print(" ".join(row))

def place_ship(board, size, ship_size):
    """Places a ship of ship_size on the board, if space is available."""
    while True:
        orientation = random.choice(['v', 'h'])
        if orientation == 'h':
            row, col = random.randint(0, len(board) - 1), random.randint(0, len(board) - ship_size)
            if all([board[row][col + i] == '.' for i in range(ship_size)]):
                for i in range(ship_size):
                    board[row][col + i] = 'S'
                break
        else:
            row, col = random.randint(0, len(board) - ship_size), random.randint(0, len(board) - 1)
            if all([board[row + i][col] == '.' for i in range(ship_size)]):
                for i in range(ship_size):
                    board[row + i][col] = 'S'
                break

def guess_coordinates(board, user_row, user_col):
    """Checks the user's guess and returns a message."""
    if board[user_row][user_col] == 'S':
        print("Hit!")
        board[user_row][user_col] = '+'
        return True
    elif board[user_row][user_col] == '.':
        print("Miss!")
        board[user_row][user_col] = 'M'
        return False
    else:
        print("You've already guessed on this location.")
        return None

def main():
    SIZE = 10
    board = initialize_board(SIZE)
    ships = [5, 4, 3, 3, 2]

    for ship in ships:
        place_ship(board, SIZE, ship)

    hidden_board = initialize_board(SIZE)
    hits, misses = 0, 0
    total_ship_squares = sum(ships)

    while hits < total_ship_squares:
        print("Current board state:")
        display_board(hidden_board)
        try:
            guess_row = int(input("Guess Row (0-9): "))
            guess_col = int(input("Guess Col (0-9): "))
        except ValueError:
            print("Invalid input, enter a number between 0 and 9.")
            continue

        if guess_row not in range(10) or guess_col not in range(10):
            print("Guesses should be between 0 and 9.")
            continue

        result = guess_coordinates(board, guess_row, guess_col)
        if result is True:
            hits += 1
        elif result is False:
            misses += 1

    print("Congratulations! You sank all battleships!")
    print(f"Total Number of Hits: {hits}")
    print(f"Total Number of Misses: {misses}")

if __name__ == "__main__":
    main()

class BankAccount:
    account_number_counter = 1000

    def __init__(self, account_holder_name, balance=0):
        self.account_number = BankAccount.account_number_counter
        BankAccount.account_number_counter += 1
        self.account_holder_name = account_holder_name
        self.balance = balance

    def deposit(self, amount):
        """
        Deposit a specified amount into the account.

        :param amount: The amount to deposit.
        """
        if amount > 0:
            self.balance += amount
            print(f"Deposited: ${amount}. New balance: ${self.balance}")
        else:
            print("Deposit amount must be positive.")

    def withdraw(self, amount):
        """
        Withdraw a specified amount from the account.

        :param amount: The amount to withdraw.
        """
        if amount > 0:
            if amount <= self.balance:
                self.balance -= amount
                print(f"Withdrew: ${amount}. New balance: ${self.balance}")
            else:
                print("Insufficient funds.")
        else:
            print("Withdrawal amount must be positive.")

    def get_balance(self):
        """
        Get the current balance of the account.

        :return: A string representing the account balance.
        """
        return f"Account Balance: ${self.balance}"

def create_account(accounts, name):
    """
    Create a new bank account.

    :param accounts: A dictionary of accounts.
    :param name: The name of the account holder.
    """
    new_account = BankAccount(name)
    accounts[new_account.account_number] = new_account
    print(f"Account created successfully. Account Number: {new_account.account_number}")

