import json

def read_json_file(filename):
    """
    Reads a JSON file and returns the parsed data.
    Handles FileNotFoundError and json.JSONDecodeError.

    :param filename: The name of the JSON file to read.
    :return: The parsed JSON data or None if an error occurred.
    """
    try:
        with open(filename, 'r') as file:
            return json.load(file)
    except FileNotFoundError:
        print(f"Error: The file '{filename}' was not found.")
        return None
    except json.JSONDecodeError:
        print(f"Error: The file '{filename}' contains invalid JSON.")
        return None

def extract_information(data, key):
    """
    Extracts and prints the value for a given key from each employee record in the data.
    Handles KeyError and TypeError.

    :param data: The list of employee records.
    :param key: The key to extract information for.
    """
    if not data:
        return
    try:
        for employee in data:
            print(employee[key])
    except KeyError:
        print(f"Error: The key '{key}' does not exist in the JSON data.")
    except TypeError:
        print("Error: The JSON data is not in the expected format.")

def main():
    """
    Main function to execute the program.
    Reads the JSON file, prompts the user for a key, and extracts the corresponding information.
    """
    filename = 'employees.json'
    data = read_json_file(filename)

    if data is not None:
        key = input("Enter the key to extract information (name, id, department, job_title): ").strip().lower()
        extract_information(data, key)

if __name__ == "__main__":
    main()

def factorial(n):
    """
    Calculate the factorial of a non-negative integer n using recursion.

    Parameters:
    n (int): A non-negative integer whose factorial is to be calculated.

    Returns:
    int: The factorial of the integer n.
    """
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

def main():
    """
    Main function to prompt the user for input, validate it, and compute the factorial.
    """
    while True:
        user_input = input("Enter a non-negative integer to calculate its factorial: ")
        try:
            number = int(user_input)
            if number < 0:
                print("Error: Please enter a non-negative integer.")
            else:
                result = factorial(number)
                print(f"The factorial of {number} is {result}.")
                break  # Exit the loop if a valid number is entered
        except ValueError:
            print("Error: Invalid input. Please enter a non-negative integer.")

if __name__ == "__main__":
    main()

import random

def get_random_word(word_list):
    """Selects a random word from a given list."""
    return random.choice(word_list)

def count_matching_letters(guess, word):
    """Counts the number of correct letters in the player's guess relative to the word."""
    return sum(1 for g, w in zip(guess, word) if g == w)

def word_guessing_game():
    """Runs the word guessing game with a predefined list of words and a limited number of attempts."""
    words = ["apple", "banana", "cherry", "date", "elderberry", "fig", "grape"]
    secret_word = get_random_word(words)
    max_attempts = 5
    attempts = 0

    print("Welcome to the Word Guessing Game!")
    print(f"You have {max_attempts} attempts to guess the word.")

    while attempts < max_attempts:
        guess = input("Please enter your guess: ").lower()

        if guess == secret_word:
            print("Congratulations! You guessed the word correctly!")
            break
        else:
            correct_letters = count_matching_letters(guess, secret_word)
            attempts += 1
            remaining_attempts = max_attempts - attempts
            print(f"Sorry, that's not the word. Your guess has {correct_letters} correct letters.")
            if remaining_attempts > 0:
                print(f"You have {remaining_attempts} attempts left.")
            else:
                print(f"Sorry, you've exhausted all your attempts. The word was: {secret_word}")

if __name__ == "__main__":
    word_guessing_game()

