class ParkingGarage:
    def __init__(self, total_slots):
        self.total_slots = total_slots
        self.available_slots = total_slots
        self.parked_cars = {}

    def park_car(self, car):
        """Parks a car in the garage if there are available slots."""
        if self.available_slots > 0:
            self.parked_cars[car.license_plate] = car
            self.available_slots -= 1
            return f"Car {car.license_plate} parked successfully."
        else:
            return "Parking garage is full. Cannot park the car."

    def remove_car(self, license_plate):
        """Removes a car from the garage using its license plate."""
        if license_plate in self.parked_cars:
            del self.parked_cars[license_plate]
            self.available_slots += 1
            return f"Car {license_plate} removed successfully."
        else:
            return "Car not found in the parking garage."

    def check_available_slots(self):
        """Returns the number of available parking slots."""
        return self.available_slots


