def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    max_divisor = int(n ** 0.5) + 1
    for d in range(3, max_divisor, 2):
        if n % d == 0:
            return False
    return True

def sum_of_primes(arr):
    """Calculate and return the sum of all prime numbers in the array."""
    return sum(x for x in arr if is_prime(x))

