import statistics

def get_numbers() -> list:
    """Prompts the user for a series of numbers and returns them as a list of floats."""
    numbers_str = input("Please enter numbers separated by spaces: ")
    return list(map(float, numbers_str.split()))

def calculate_mean(numbers: list) -> float:
    """Calculates and returns the mean of the given list of numbers."""
    return statistics.mean(numbers)

def calculate_median(numbers: list) -> float:
    """Calculates and returns the median of the given list of numbers."""
    return statistics.median(numbers)

def calculate_mode(numbers: list) -> list:
    """Calculates and returns the mode(s) of the given list of numbers."""
    frequency = Counter(numbers)
    max_count = max(frequency.values())
    return sorted([num for num, count in frequency.items() if count == max_count])

