class TreeNode:
    def __init__(self, key):
        self.left = None
        self.right = None
        self.val = key

class BinarySearchTree:
    def __init__(self):
        self.root = None

    def insert(self, key):
        """Insert a new key into the BST."""
        if self.root is None:
            self.root = TreeNode(key)
        else:
            self._insert_recursively(self.root, key)

    def _insert_recursively(self, node, key):
        """Helper function to insert a new key into the BST recursively."""
        if key < node.val:
            if node.left is None:
                node.left = TreeNode(key)
            else:
                self._insert_recursively(node.left, key)
        else:
            if node.right is None:
                node.right = TreeNode(key)
            else:
                self._insert_recursively(node.right, key)

    def in_order_traversal(self):
        """Return the in-order traversal of the BST as a list."""
        result = []
        self._in_order_recursively(self.root, result)
        return result

    def _in_order_recursively(self, node, result):
        """Helper function to perform in-order traversal of the BST recursively."""
        if node is not None:
            self._in_order_recursively(node.left, result)
            result.append(node.val)
            self._in_order_recursively(node.right, result)

