import string

def count_words(filename, stop_words):
    """
    Counts the frequency of each word in a file, excluding stop words.

    Args:
    filename (str): The name of the file to read.
    stop_words (set): A set of words to exclude from the count.

    Returns:
    list of tuples: A list of (word, frequency) pairs sorted by frequency in descending order.
    """
    try:
        with open(filename, 'r') as file:
            word_count = {}
            for line in file:
                line = line.translate(str.maketrans('', '', string.punctuation)).lower()
                words = line.split()
                for word in words:
                    if word not in stop_words:
                        word_count[word] = word_count.get(word, 0) + 1
            return sorted(word_count.items(), key=lambda x: x[1], reverse=True)
    except FileNotFoundError:
        print(f"Error: The file {filename} does not exist.")
    except IOError:
        print(f"Error: An error occurred while reading the file {filename}.")

