def cache_factorial(func):
    """
    Decorator to cache the results of the factorial function.
    """
    cache = {}  # This will store the calculated factorial results

    def wrapper(n):
        """
        Wrapper function to check cache and compute factorial if necessary.
        """
        if n in cache:
            return cache[n]  # Return cached result
        else:
            result = func(n)  # Calculate factorial for the first time
            cache[n] = result  # Store the result in cache
            return result

    return wrapper

@cache_factorial
def factorial(n):
    """
    Calculate the factorial of a number n.
    """
    if n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

