def sanitize_input(user_input):
    """
    Sanitizes the input string by removing leading and trailing whitespace,
    and eliminating specified characters: \\t, \\n, <, >, &, ', and ".

    :param user_input: The string to be sanitized.
    :return: The sanitized string.
    """
    chars_to_remove = {'\t', '\n', '<', '>', '&', "'", '"'}
    start = 0
    end = len(user_input) - 1

    while start <= end and user_input[start] in {' ', '\t', '\n'}:
        start += 1

    while end >= start and user_input[end] in {' ', '\t', '\n'}:
        end -= 1

    sanitized = []

    for i in range(start, end + 1):
        if user_input[i] not in chars_to_remove:
            sanitized.append(user_input[i])

    return ''.join(sanitized)

