class TreeNode:
    def __init__(self, key):
        self.left = None
        self.right = None
        self.val = key

class Tree:
    def __init__(self):
        self.root = None

    def insert(self, key):
        """Insert a new node with the given key into the BST."""
        if self.root is None:
            self.root = TreeNode(key)
        else:
            self._insert(self.root, key)

    def _insert(self, node, key):
        if key < node.val:
            if node.left is None:
                node.left = TreeNode(key)
            else:
                self._insert(node.left, key)
        else:
            if node.right is None:
                node.right = TreeNode(key)
            else:
                self._insert(node.right, key)

    def delete(self, key):
        """Delete the node with the given key from the BST."""
        self.root = self._delete(self.root, key)

    def _delete(self, node, key):
        if node is None:
            return node

        if key < node.val:
            node.left = self._delete(node.left, key)
        elif key > node.val:
            node.right = self._delete(node.right, key)
        else:
            if node.left is None:
                return node.right
            elif node.right is None:
                return node.left

            temp = self._min_value_node(node.right)
            node.val = temp.val
            node.right = self._delete(node.right, temp.val)

        return node

    def _min_value_node(self, node):
        current = node
        while current.left is not None:
            current = current.left
        return current

    def in_order_traversal(self):
        """Return the keys of the BST in sorted order using in-order traversal."""
        return self._in_order_traversal(self.root)

    def _in_order_traversal(self, node):
        res = []
        if node:
            res = self._in_order_traversal(node.left)
            res.append(node.val)
            res = res + self._in_order_traversal(node.right)
        return res

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    bst = Tree()
    i = 1
    while i < len(data):
        command = data[i]
        if command == 'I':
            bst.insert(int(data[i + 1]))
            i += 2
        elif command == 'D':
            bst.delete(int(data[i + 1]))
            i += 2
        elif command == 'T':
            print(' '.join(map(str, bst.in_order_traversal())))
            i += 1

