import hashlib
import os

def hash_file(filePath, chunk_size=1024):
    """
    Hashes a file using the SHA-256 algorithm and returns the hexadecimal digest.

    Parameters:
    filePath (str): Path to the file that needs to be hashed.
    chunk_size (int): Size of the chunk to read from the file.

    Returns:
    str: Hexadecimal digest of the SHA-256 hash, or None if an error occurs.
    """
    sha256_hash = hashlib.sha256()
    try:
        with open(filePath, "rb") as f:
            for byte_block in iter(lambda: f.read(chunk_size), b""):
                sha256_hash.update(byte_block)
        return sha256_hash.hexdigest()
    except FileNotFoundError:
        print("Error: The file was not found. Please check the file path.")
    except PermissionError:
        print("Error: Permission denied. Please check your permissions for the file.")
    except IOError:
        print("Error: An I/O error occurred while handling the file.")
    except Exception as e:
        print(f"Unexpected error: {e}")
    return None

if __name__ == "__main__":
    file_path = input().strip()
    chunk_size = int(input().strip())
