import json
import ipaddress
from typing import Any, Dict

def read_config(filename: str) -> Dict[str, Any]:
    """Reads a JSON configuration file and returns its contents as a dictionary."""
    with open(filename, 'r') as file:
        return json.load(file)

def write_config(filename: str, config: Dict[str, Any]) -> None:
    """Writes the given configuration dictionary to a JSON file with pretty printing."""
    with open(filename, 'w') as file:
        json.dump(config, file, indent=4)

def get_valid_ip_address(prompt: str) -> str:
    """Prompts the user for a valid IPv4 address and returns it."""
    while True:
        ip = input(prompt)
        try:
            ipaddress.IPv4Address(ip)
            return ip
        except ValueError:
            print("Invalid IPv4 address")

def get_valid_port(prompt: str) -> int:
    """Prompts the user for a valid port number (1-65535) and returns it."""
    while True:
        port_str = input(prompt)
        if port_str.isdigit():
            port = int(port_str)
            if 1 <= port <= 65535:
                return port
        print("Invalid port number. It must be an integer between 1 and 65535.")

def get_valid_max_connections(prompt: str) -> int:
    """Prompts the user for a valid maximum number of connections (greater than 0) and returns it."""
    while True:
        max_connections_str = input(prompt)
        if max_connections_str.isdigit():
            max_connections = int(max_connections_str)
            if max_connections > 0:
                return max_connections
        print("Invalid max connections. It must be an integer greater than 0.")

def get_valid_use_ssl(prompt: str) -> bool:
    """Prompts the user for a valid boolean value for SSL usage and returns it."""
    while True:
        use_ssl = input(f"{prompt} (True/False): ").lower()
        if use_ssl in ['true', 'false']:
            return use_ssl == 'true'
        print("Invalid use_ssl value. It must be 'True' or 'False'.")

def update_config(filename: str) -> None:
    """Updates the configuration file with new values provided by the user after validation."""
    config = read_config(filename)

    config['ip_address'] = get_valid_ip_address(f"Enter a new IP address (current: {config['ip_address']}): ")
    config['port'] = get_valid_port(f"Enter a new port number (current: {config['port']}): ")
    config['max_connections'] = get_valid_max_connections(f"Enter a new max connections (current: {config['max_connections']}): ")
    config['use_ssl'] = get_valid_use_ssl(f"Enable SSL? (current: {config['use_ssl']}): ")

    write_config(filename, config)
    print("Configuration updated successfully!")

if __name__ == "__main__":
    update_config('config.json')

