    else:
        inventory[name] = quantity
    print(f"Added {quantity} {name}(s).")

def remove_item(inventory, name, quantity):
    """
    Removes a specified quantity of an item from the inventory.
    Prints an appropriate message if the item does not exist or
    if the specified quantity exceeds the available quantity.
    """
    if name in inventory and inventory[name] >= quantity:
        inventory[name] -= quantity
        print(f"Removed {quantity} {name}(s).")
        if inventory[name] == 0:
