from email_validator import validate_email, EmailNotValidError

app = Flask(__name__)

@app.route('/user', methods=['POST'])
def user_input():
    """
    Handles POST requests to /user endpoint with JSON payload containing user information.
    Validates the input and returns the user information if valid, otherwise returns an error message.
    """
    data = request.get_json()

    if not data:
        return jsonify({"error": "Invalid JSON payload"}), 400

    name = data.get('name')
    email = data.get('email')
    age = data.get('age')

    if not name:
        return jsonify({"error": "Name is required"}), 400

    if not email:
        return jsonify({"error": "Email is required"}), 400

    try:
        valid = validate_email(email)
        email = valid.email
    except EmailNotValidError as e:
        return jsonify({"error": str(e)}), 400

    if not isinstance(age, int) or age <= 0:
        return jsonify({"error": "Age must be a positive integer"}), 400

    return jsonify(name=name, email=email, age=age)

