import os
import shutil

def create_directory_structure(root_directory, folder_name, operation="create", delete_file=False):
    """
    Manages directory operations within a specified root directory, handling symbolic links correctly.

    Parameters:
    - root_directory (str): The absolute path of the root directory.
    - folder_name (str): The name of the folder to be created, renamed, or deleted.
    - operation (str): The operation to perform ("create", "rename", "delete"). Defaults to "create".
    - delete_file (bool): Whether to delete the folder and its contents (True) or just the folder itself (False). Defaults to False.

    Returns:
    - None
    """
    # Ensure the root_directory is in absolute form and resolve any symbolic links
    root_directory = os.path.realpath(os.path.abspath(root_directory))

    # Construct the full path for the folder
    folder_path = os.path.join(root_directory, folder_name)

    # Handle the operation
    if operation == "create":
        if not os.path.exists(folder_path):
            os.makedirs(folder_path)
            print(f"Folder '{folder_name}' created in '{root_directory}'.")
        else:
            print(f"Folder '{folder_name}' already exists in '{root_directory}'.")

    elif operation == "rename":
        if os.path.exists(folder_path):
            new_folder_name = input(f"Enter the new name for '{folder_name}': ")
            new_folder_path = os.path.join(root_directory, new_folder_name)
            os.rename(folder_path, new_folder_path)
            print(f"Folder renamed to '{new_folder_name}'.")
        else:
            print(f"Folder '{folder_name}' does not exist in '{root_directory}', so cannot be renamed.")

    elif operation == "delete":
        if os.path.exists(folder_path):
            if delete_file:
                shutil.rmtree(folder_path)
                print(f"Folder '{folder_name}' and all its contents have been deleted.")
            else:
                if not os.listdir(folder_path):
                    os.rmdir(folder_path)
                    print(f"Folder '{folder_name}' has been deleted.")
                else:
                    print(f"Folder '{folder_name}' is not empty. Use delete_file=True to delete it with all contents.")
        else:
            print(f"Folder '{folder_name}' does not exist in '{root_directory}', so cannot be deleted.")
    else:
        print("Invalid operation. Operation should be 'create', 'rename', or 'delete'.")

class BankAccount:
    account_number_counter = 1000  # Counter to create unique account numbers

    def __init__(self, account_holder_name, initial_deposit):
        """
        Initialize a new bank account with a unique account number, account holder's name, and initial deposit.
        """
        self.account_number = BankAccount.account_number_counter
        BankAccount.account_number_counter += 1
        self.account_holder_name = account_holder_name
        self.balance = initial_deposit

    def deposit(self, amount):
        """
        Deposit a specified amount into the account.
        """
        if amount > 0:
            self.balance += amount
            print(f"Deposited ${amount}. New balance: ${self.balance}.")
        else:
            print("Deposit amount must be positive.")

    def withdraw(self, amount):
        """
        Withdraw a specified amount from the account with a 1% withdrawal fee.
        """
        withdrawal_fee = amount * 0.01  # 1% withdrawal fee
        total_withdrawal = amount + withdrawal_fee
        if amount > 0 and total_withdrawal <= self.balance:
            self.balance -= total_withdrawal
            print(f"Withdrew ${amount} with a fee of ${withdrawal_fee:.1f}. New balance: ${self.balance}.")
        elif amount <= 0:
            print("Withdrawal amount must be positive.")
        else:
            print("Insufficient balance for withdrawal including the fee.")

    def check_balance(self):
        """
        Check and print the balance of the account.
        """
        print(f"Account Balances for {self.account_holder_name} (Account #{self.account_number}): ${self.balance}.")

    def close_account(self):
        """
        Close the account and print the remaining balance.
        """
        print(f"Closing account {self.account_number} for {self.account_holder_name}. Returning balance of ${self.balance}.")
        self.balance = 0


