def find_value_by_key(nested_dict, target_key):
    """
    Recursively searches for a target_key in a nested dictionary and returns its value.
    If the key is not found, returns None.

    :param nested_dict: The dictionary to search within.
    :param target_key: The key to search for.
    :return: The value associated with the target_key if found, otherwise None.
    """
    if not isinstance(nested_dict, dict):
        return None

    for key, value in nested_dict.items():
        if key == target_key:
            return value
        if isinstance(value, dict):
            result = find_value_by_key(value, target_key)
            if result is not None:
                return result
    return None

import unittest

class TestFindValueByKey(unittest.TestCase):
    def test_find_key_in_nested_dict(self):
        self.assertEqual(find_value_by_key({'a': {'b': 1}}, 'b'), 1)
        self.assertEqual(find_value_by_key({'x': {'y': {'z': 2}}}, 'z'), 2)

    def test_key_not_found(self):
        self.assertIsNone(find_value_by_key({'a': {'b': 1}}, 'c'))
        self.assertIsNone(find_value_by_key({'x': {'y': {'z': 2}}}, 'a'))

    def test_non_dict_input(self):
        self.assertIsNone(find_value_by_key([1, 2, 3], 'a'))
        self.assertIsNone(find_value_by_key('string', 's'))
        self.assertIsNone(find_value_by_key(123, '1'))

def parse_input(input_str, conversion_fn=None):
    """
    Attempts to convert the input string using the provided conversion function.
    If conversion fails, returns the original string.

    Parameters:
    - input_str (str): The string to be converted.
    - conversion_fn (function, optional): The function to use for conversion.

    Returns:
    - The converted value if successful, otherwise the original string.
    """
    vowels = {'a', 'e', 'i', 'o', 'u'}

    if conversion_fn is None:
        try:
            return int(input_str)
        except ValueError:
            try:
                return float(input_str)
            except ValueError:
                return input_str
    else:
        if conversion_fn in (int, float, str):
            try:
                return conversion_fn(input_str)
            except ValueError:
                return input_str
        else:
            distinct_vowels = set(input_str) & vowels
            if len(distinct_vowels) >= 2:
                return conversion_fn(input_str)
            else:
                return input_str

class Graph:
    def __init__(self):
        """Initialize an empty graph represented as an adjacency list."""
        self.graph = {}

    def add_vertex(self, vertex):
        """Add a vertex to the graph if it does not already exist."""
        if vertex not in self.graph:
            self.graph[vertex] = []

    def add_edge(self, vertex1, vertex2):
        """Add an undirected edge between vertex1 and vertex2."""
        if vertex1 in self.graph:
            self.graph[vertex1].append(vertex2)
        else:
            self.graph[vertex1] = [vertex2]
        if vertex2 in self.graph:
            self.graph[vertex2].append(vertex1)
        else:
            self.graph[vertex2] = [vertex1]

    def dfs(self, start_vertex, visited=None):
        """Perform a depth-first search starting from start_vertex and print the traversal."""
        if visited is None:
            visited = set()
        visited.add(start_vertex)
        print(start_vertex, end=" ")
        for neighbor in self.graph[start_vertex]:
            if neighbor not in visited:
                self.dfs(neighbor, visited)


