def process_input(input_str: str) -> float:
    """
    Processes a string containing numerical values and special characters, returning the sum of the values.
    Handles nested parentheses and special characters with specific float values.
    """
    def evaluate_expression(tokens):
        stack = []
        i = 0
        while i < len(tokens):
            token = tokens[i]
            if token.replace('.', '', 1).isdigit():
                stack.append(float(token))
            elif token == '(':
                count = 1
                start = i + 1
                while i < len(tokens):
                    i += 1
                    if tokens[i] == '(':
                        count += 1
                    elif tokens[i] == ')':
                        count -= 1
                    if count == 0:
                        break
                sub_expression = tokens[start:i]
                stack.append(evaluate_expression(sub_expression))
            elif token == 'A':
                stack.append(1.5)
            elif token == 'B':
                stack.append(2.5)
            elif token == 'C':
                stack.append(3.5)
            else:
                stack.append(1.0)
            i += 1
        return sum(stack)

    if not input_str:
        return 0.0

    tokens = input_str.replace(' ', '').split(',')
    return evaluate_expression(tokens)

