    object_or_direction = command_parts[1].lower()

    if action == 'go':
        if object_or_direction in locations[current_location]['connected_areas']:
            global current_location
            current_location = locations[current_location]['connected_areas'][object_or_direction]
            display_location_details()
        else:
            print(f"You can't go {object_or_direction}. Please choose a valid direction.")
    elif action == 'take':
        if object_or_direction in locations[current_location]['objects']:
            print(f"You have taken the {object_or_direction}.")
            locations[current_location]['objects'].remove(object_or_direction)
        else:
            print(f"You can't take the {object_or_direction} from here.")
    else:
        print("Invalid action. Try 'go [direction]' or 'take [object]'.")
        return False

    return True

