import csv

def calculate_average_from_csv():
    """
    Prompts the user for a CSV file name and a column name, then calculates and prints the average value of the specified column.
    Handles errors such as file not found, invalid column name, and non-numeric values in the column.
    """
    file_name = input("Please enter the CSV file name: ")
    column_name = input("Please enter the column name to calculate the average from: ")

    try:
        total = 0
        count = 0

        with open(file_name, mode='r') as file:
            csv_reader = csv.DictReader(file)

            for row in csv_reader:
                try:
                    total += float(row[column_name])
                    count += 1
                except ValueError:
                    print(f"Skipping row with invalid value in column '{column_name}': {row[column_name]}")
                except KeyError:
                    print(f"Column '{column_name}' does not exist in the CSV file.")
                    return

        if count > 0:
            average = total / count
            print(f"The average value of the column '{column_name}' is: {average}")
        else:
            print(f"No valid data found in column '{column_name}' to calculate an average.")

    except FileNotFoundError:
        print(f"The file '{file_name}' does not exist.")
    except Exception as e:
        print(f"An error occurred: {e}")

calculate_average_from_csv()

