        self.employees = {}

    def add_employee(self, name, age, department, salary):
        """Add a new employee to the database."""
        if name in self.employees:
            print(f"Employee {name} already exists.")
            return
        self.employees[name] = Employee(name, age, department, salary)
        print(f"Employee {name} added successfully.")

    def delete_employee(self, name):
        """Delete an employee from the database."""
        if name not in self.employees:
            print(f"Employee {name} does not exist.")
            return
        del self.employees[name]
        print(f"Employee {name} deleted successfully.")

