        """Update an employee's details in the database."""
        if name not in self.employees:
            print(f"Employee {name} does not exist.")
            return
        employee = self.employees[name]
        if age is not None:
            employee.age = age
        if department is not None:
            employee.department = department
        if salary is not None:
            employee.salary = salary
        print(f"Employee {name} updated successfully.")

    def get_employee(self, name):
        """Retrieve an employee's details from the database."""
        if name not in self.employees:
            print(f"Employee {name} does not exist.")
            return None
        return self.employees[name]

    def list_all_employees(self):
        """List all employees in the database."""
        if not self.employees:
            print("No employees in the database.")
            return
        for employee in self.employees.values():
