class TaskManager:
    def __init__(self, max_tasks):
        """
        Initializes the TaskManager with a maximum number of tasks allowed before the deadline.

        :param max_tasks: The maximum number of tasks that can be added without exceeding the deadline.
        """
        self.tasks = set()  # Using set for tasks to ensure uniqueness
        self.max_tasks = max_tasks

