        """
        Adds a task to the project. Raises ValueError if adding the task would exceed the deadline.

        :param task: The new task to be added.
        """
        if len(self.tasks) >= self.max_tasks:
            raise ValueError("Adding this task would exceed the project deadline.")

        if task in self.tasks:
            print(f"Task '{task}' already exists and will not be added again.")
        else:
            self.tasks.add(task)
            print(f"Task '{task}' added successfully.")

    def list_tasks(self):
        """
        Lists all tasks.

        :return: A list of all current tasks.
        """
        return list(self.tasks)

