import os
import json

class ToDoList:
    def __init__(self, filename="todo.json"):
        """
        Initializes the ToDoList with a specified filename for task persistence.
        """
        self.filename = filename
        self.tasks = []
        self.load_tasks()

    def add_task(self, task):
        """
        Adds a new task to the list and saves the updated list to the file.
        """
        self.tasks.append(task)
        self.save_tasks()
        print(f"Task '{task}' added.")

    def remove_task(self, task_number):
        """
        Removes a task from the list by its index and saves the updated list to the file.
        """
        if 1 <= task_number <= len(self.tasks):
            removed_task = self.tasks.pop(task_number - 1)
            self.save_tasks()
            print(f"Task '{removed_task}' removed.")
        else:
            print("Invalid task number.")

    def list_tasks(self):
        """
        Lists all tasks in the list with their indices.
        """
        if not self.tasks:
            print("No tasks.")
        else:
            print("Tasks:")
            for index, task in enumerate(self.tasks, start=1):
                print(f"{index}. {task}")

    def save_tasks(self):
        """
        Saves the current list of tasks to a file in JSON format.
        """
        with open(self.filename, 'w') as file:
            json.dump(self.tasks, file, indent=4)

    def load_tasks(self):
        """
        Loads tasks from a file if it exists. Initializes an empty list if the file does not exist or is corrupted.
        """
        if os.path.exists(self.filename):
            with open(self.filename, 'r') as file:
                try:
                    self.tasks = json.load(file)
                except json.JSONDecodeError:
                    print("Error reading task file. Tasks will be reset.")
                    self.tasks = []

def main():
    """
    Main function to run the ToDoList application, handling user input and commands.
    """
    todo_list = ToDoList()
    while True:
        command = input("Enter command (add, remove, list, exit): ").strip().lower()
        if command.startswith('add '):
            task = command[4:].strip()
            if task:
                todo_list.add_task(task)
        elif command.startswith('remove '):
            try:
                task_number = int(command[7:])
                todo_list.remove_task(task_number)
            except ValueError:
                print("Please enter a valid number.")
        elif command == 'list':
            todo_list.list_tasks()
        elif command == 'exit':
            break
        else:
            print("Invalid command. Type 'add', 'remove', 'list', or 'exit'.")

if __name__ == "__main__":
    main()

class ShoppingCart:
    def __init__(self):
        self.cart = {}

    def add_item(self, item, quantity=1):
        """
        Adds a specified quantity of an item to the cart.
        If the item already exists, increases its quantity.
        """
        if item in self.cart:
            self.cart[item] += quantity
        else:
            self.cart[item] = quantity
        print(f"Added {quantity} {item}(s).")

    def view_cart(self):
        """
        Displays all items in the cart along with their quantities.
        If the cart is empty, displays an appropriate message.
        """
        if not self.cart:
            print("Your shopping cart is empty.")
        else:
            print("Items in your shopping cart:")
            for item, quantity in self.cart.items():
                print(f"{item}: {quantity}")

    def remove_item(self, item, quantity=1):
        """
        Removes a specified quantity of an item from the cart.
        Handles cases where the specified quantity exceeds the available quantity.
        """
        if item in self.cart:
            if self.cart[item] > quantity:
                self.cart[item] -= quantity
                print(f"Removed {quantity} {item}(s).")
            elif self.cart[item] == quantity:
                del self.cart[item]
                print(f"Removed {quantity} {item}(s). Item is now removed from the cart.")
            else:
                print(f"Cannot remove {quantity} {item}(s), only {self.cart[item]} available.")
        else:
            print(f"{item} not found in the cart.")

