from collections import Counter
import string

def clean_word(word):
    """Removes punctuation from a given word and converts it to lowercase."""
    return word.translate(str.maketrans('', '', string.punctuation)).lower()

def count_words(filename, stop_words):
    """Counts the frequency of each word in a file, excluding stop words."""
    word_counts = Counter()
    with open(filename, 'r') as file:
        for line in file:
            words = [clean_word(word) for word in line.split()]
            word_counts.update(word for word in words if word not in stop_words)
    return word_counts

def write_frequencies(word_counts, output_filename):
    """Writes word frequencies to a file, sorted by frequency in descending order."""
    with open(output_filename, 'w') as file:
        for word, count in word_counts.most_common():
            file.write(f'{word}: {count}\n')

def main():
    stop_words = ['the', 'and', 'is', 'in', 'a', 'to', 'of', 'it', 'with', 'on', 'for', 'at', 'by', 'an']
    input_filename = input().strip()
    output_filename = input().strip()

    word_counts = count_words(input_filename, stop_words)
    write_frequencies(word_counts, output_filename)

