def confirm_action(prompt: str) -> bool:
    """
    Prompts the user with a given message and requests confirmation for an action.
    The user can confirm by entering 'yes', 'y', 'yeah', 'aye', or 'okay'.
    The user can deny by entering 'no', 'n', 'nope', 'nay', or 'nah'.
    Continues to prompt until a valid response is received.

    :param prompt: The message to display to the user.
    :return: True if the user confirms the action, False if the user denies it.
    """
    affirmative_responses = {"yes", "y", "yeah", "aye", "okay"}
    negative_responses = {"no", "n", "nope", "nay", "nah"}

    while True:
        user_input = input(prompt).strip().lower()
        if user_input in affirmative_responses:
            return True
        elif user_input in negative_responses:
            return False
        else:
            print("Invalid input. Please enter an affirmative or negative response.")

def gcd(a, b):
    """Compute the greatest common divisor of a and b using the Euclidean algorithm."""
    while b:
        a, b = b, a % b
    return a

