import re
from collections import defaultdict
from math import log

def preprocess_text(text):
    """
    Preprocesses the text by converting it to lowercase, removing punctuation, and splitting into words.
    """
    text = re.sub(r'[^\w\s]', '', text.lower())
    return text.split()

def create_vocabulary(reviews):
    """
    Creates a vocabulary from the list of reviews.
    """
    vocabulary = set()
    for _, text in reviews:
        words = preprocess_text(text)
        vocabulary.update(words)
    return list(vocabulary)

def word_counts(reviews, vocabulary):
    """
    Counts the occurrences of each word in the reviews for each sentiment label.
    """
    counts = defaultdict(lambda: {"positive": 0, "negative": 0, "neutral": 0})
    label_counts = {"positive": 0, "negative": 0, "neutral": 0}

    for label, text in reviews:
        words = preprocess_text(text)
        label_counts[label] += 1
        for word in words:
            counts[word][label] += 1
    return counts, label_counts

def train_language_model(reviews, vocabulary):
    """
    Trains a Naive Bayes language model using the given reviews and vocabulary.
    """
    counts, label_counts = word_counts(reviews, vocabulary)

    total_reviews = len(reviews)
    prior_probabilities = {label: count / total_reviews for label, count in label_counts.items()}

    likelihood_probabilities = {}
    for word in vocabulary:
        for label in ["positive", "negative", "neutral"]:
            word_count_in_label = counts[word][label]
            total_words_in_label = sum(counts[word][label] for word in counts.keys())
            likelihood_probabilities[(word, label)] = (word_count_in_label + 1) / (total_words_in_label + len(vocabulary))
    return prior_probabilities, likelihood_probabilities

def predict(text, prior_probabilities, likelihood_probabilities, vocabulary):
    """
    Predicts the sentiment of a given text using the trained language model.
    """
    words = preprocess_text(text)
    probabilities = defaultdict(float)

    for label in prior_probabilities:
        probabilities[label] = log(prior_probabilities[label])
        for word in words:
            if word in vocabulary:
                probabilities[label] += log(likelihood_probabilities.get((word, label), 1/len(vocabulary)))

    return max(probabilities, key=probabilities.get)

