def calculate_mean(numbers):
    """
    Calculate the mean of a list of numbers.

    :param numbers: List of numbers
    :return: Mean of the numbers
    """
    total = 0
    for number in numbers:
        total += number
    return total / len(numbers)

def calculate_median(numbers):
    """
    Calculate the median of a list of numbers.

    :param numbers: List of numbers
    :return: Median of the numbers
    """
    numbers_sorted = []
    for i in range(len(numbers)):
        inserted = False
        for j in range(len(numbers_sorted)):
            if numbers[i] < numbers_sorted[j]:
                numbers_sorted.insert(j, numbers[i])
                inserted = True
                break
        if not inserted:
            numbers_sorted.append(numbers[i])

    n = len(numbers_sorted)
    mid = n // 2
    if n % 2 == 0:
        return (numbers_sorted[mid - 1] + numbers_sorted[mid]) / 2
    else:
        return numbers_sorted[mid]

def calculate_mode(numbers):
    """
    Calculate the mode of a list of numbers.

    :param numbers: List of numbers
    :return: Mode of the numbers or None if no unique mode exists
    """
    frequency = {}
    for number in numbers:
        if number in frequency:
            frequency[number] += 1
        else:
            frequency[number] = 1

    max_count = 0
    for count in frequency.values():
        if count > max_count:
            max_count = count

    modes = []
    for number, count in frequency.items():
        if count == max_count:
            modes.append(number)

    if len(modes) == len(numbers):
        return None  # No mode if all values occur the same number of times
    return modes[0] if len(modes) == 1 else modes

def main():
    input_sequence = input("Enter a sequence of numbers separated by spaces: ")
    numbers = list(map(float, input_sequence.split()))

    mean = calculate_mean(numbers)
    median = calculate_median(numbers)
    mode = calculate_mode(numbers)

    print(f"{mean}")
    print(f"{median}")
    print("No mode" if mode is None else mode)

if __name__ == "__main__":
    main()

