def can_form_palindrome(s):
    """
    Determines if the characters of the string `s` can be rearranged to form a palindrome.

    :param s: A string consisting of lowercase English letters.
    :return: True if the string can be rearranged to form a palindrome, False otherwise.
    """
    char_count = {}
    for c in s:
        char_count[c] = char_count.get(c, 0) + 1

    odd_counts = sum(1 for count in char_count.values() if count % 2 != 0)

    return odd_counts <= 1

