import random

def is_prime(n):
    """
    Check if a number is prime.

    :param n: Integer to check for primality.
    :return: True if n is prime, False otherwise.
    """
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

