import os
import re
import ast

def is_snake_case(name):
    """
    Check if the given name is in snake_case.

    :param name: The name to check.
    :return: True if the name is in snake_case, False otherwise.
    """
    return bool(re.match(r'^[a-z]+(_[a-z]+)*$', name))

def check_pep8_naming_convention(filename):
    """
    Check if all function names in the given Python file follow the PEP 8 naming convention.

    :param filename: The path to the Python file.
    :return: A list of function names that do not follow the PEP 8 naming convention.
    """
    with open(filename, 'r', encoding='utf-8') as file:
        tree = ast.parse(file.read())

    non_snake_case_functions = []

    for node in ast.walk(tree):
        if isinstance(node, ast.FunctionDef):
            if not is_snake_case(node.name):
                non_snake_case_functions.append(node.name)
        elif isinstance(node, ast.ClassDef):
            for subnode in node.body:
                if isinstance(subnode, ast.FunctionDef):
                    if not is_snake_case(subnode.name):
                        non_snake_case_functions.append(subnode.name)

    return non_snake_case_functions

def scan_directory_for_pep8(directory):
    """
    Scan the given directory for Python files and check their function names for PEP 8 compliance.

    :param directory: The path to the directory to scan.
    :return: A dictionary mapping file paths to lists of non-conforming function names.
    """
    non_snake_case_functions_combined = {}
    for root, dirs, files in os.walk(directory):
        for file in files:
            if file.endswith('.py'):
                filename = os.path.join(root, file)
                problematic_functions = check_pep8_naming_convention(filename)
                if problematic_functions:
                    non_snake_case_functions_combined[filename] = problematic_functions

    return non_snake_case_functions_combined

def main():
    """
    Main function to execute the script.
    """
    directory_path = input("Enter the directory path: ")
    non_conforming_functions = scan_directory_for_pep8(directory_path)

    if non_conforming_functions:
        print("The following functions use non-PEP8 naming convention:")
        for file, functions in non_conforming_functions.items():
            print(f"File: {file}")
            for func in functions:
                print(f"  - Function: {func}")
    else:
        print("All function names conform to the PEP 8 naming conventions.")

if __name__ == "__main__":
    main()

