def expand_input(input_data):
    """
    Expands the input data into a flattened list of integers.

    Args:
    input_data: Can be a 2-tuple of integers, a list of integers, or a nested list containing integers and 2-tuples.

    Returns:
    A list of integers if the input is valid, otherwise a string "Invalid input".
    """
    def expand(value):
        if isinstance(value, int):
            return [value]
        elif isinstance(value, tuple) and len(value) == 2 and all(isinstance(x, int) for x in value):
            return list(range(value[0], value[1] + 1))
        elif isinstance(value, list):
            result = []
            for item in value:
                result.extend(expand(item))
            return result
        else:
            raise ValueError("Invalid input")

    try:
        return expand(input_data)
    except ValueError as e:
        return str(e)

import random

def partition(arr, low, high):
    """
    Partitions the array around a pivot element and returns the index of the pivot.
    """
    pivot_index = random.randint(low, high)
    pivot_value = arr[pivot_index]
    arr[pivot_index], arr[high] = arr[high], arr[pivot_index]
    store_index = low
    for i in range(low, high):
        if arr[i] < pivot_value:
            arr[i], arr[store_index] = arr[store_index], arr[i]
            store_index += 1
    arr[store_index], arr[high] = arr[high], arr[store_index]
    return store_index

def quickselect(arr, low, high, k_smallest):
    """
    Returns the k-th smallest element in the array within the range [low, high].
    """
    if low == high:
        return arr[low]

    pivot_index = partition(arr, low, high)

    if k_smallest == pivot_index:
        return arr[k_smallest]
    elif k_smallest < pivot_index:
        return quickselect(arr, low, pivot_index - 1, k_smallest)
    else:
        return quickselect(arr, pivot_index + 1, high, k_smallest)

def findKthSmallest(arr, k):
    """
    Finds the k-th smallest element in the array.
    """
    return quickselect(arr, 0, len(arr) - 1, k - 1)

