class SegmentTree:
    def __init__(self, n):
        """Initialize the segment tree and lazy propagation arrays."""
        self.n = n
        self.tree = [0] * (4 * n)
        self.lazy = [0] * (4 * n)

    def push(self, node, start, end):
        """Push the lazy value to the children nodes if necessary."""
        if self.lazy[node] != 0:
            self.tree[node] += self.lazy[node]
            if start != end:
                self.lazy[2 * node] += self.lazy[node]
                self.lazy[2 * node + 1] += self.lazy[node]
            self.lazy[node] = 0

    def update_range(self, l, r, val, node=1, start=0, end=None):
        """Update the range [l, r] by adding val to each element in this range."""
        if end is None:
            end = self.n - 1

        self.push(node, start, end)
        if start > r or end < l:
            return

        if start >= l and end <= r:
            self.tree[node] += val
            if start != end:
                self.lazy[2 * node] += val
                self.lazy[2 * node + 1] += val
            return

        mid = (start + end) // 2
        self.update_range(l, r, val, 2 * node, start, mid)
        self.update_range(l, r, val, 2 * node + 1, mid + 1, end)
        self.tree[node] = max(self.tree[2 * node], self.tree[2 * node + 1])

    def query(self, l, r, node=1, start=0, end=None):
        """Query the maximum element in the range [l, r]."""
        if end is None:
            end = self.n - 1

        self.push(node, start, end)
        if start > r or end < l:
            return float('-inf')

        if start >= l and end <= r:
            return self.tree[node]

        mid = (start + end) // 2
        left_max = self.query(l, r, 2 * node, start, mid)
        right_max = self.query(l, r, 2 * node + 1, mid + 1, end)
        return max(left_max, right_max)

