class TreeNode:
    def __init__(self, key):
        self.left = None
        self.right = None
        self.val = key

def verticalOrder(root):
    """
    Perform a vertical order traversal of a binary tree.

    :param root: TreeNode, the root of the binary tree
    :return: List[int], the values of the nodes in vertical order
    """
    if root is None:
        return []

    nodes = {}
    queue = [(root, 0)]

    while queue:
        node, level = queue.pop(0)

        if level not in nodes:
            nodes[level] = []

        nodes[level].append(node.val)

        if node.left:
            queue.append((node.left, level - 1))
        if node.right:
            queue.append((node.right, level + 1))

    result = []
    for level in sorted(nodes.keys()):
        result.extend(nodes[level])

    return result

