        """
        Cancels a booked seat.
        :param seat_number: The seat number to cancel.
        """
        if seat_number in self.booked_seats:
            self.booked_seats.remove(seat_number)
            print(f"Seat {seat_number} cancelled successfully.")
        else:
            print(f"Seat {seat_number} is not booked. Cannot cancel.")

