def parse_input(s):
    """
    Parses a string into a list of integers, floats, and nested lists.

    :param s: A string containing integers, floats, and nested lists separated by commas.
    :return: A list of parsed values.
    """
    def convert(value):
        try:
            return int(value)
        except ValueError:
            try:
                return float(value)
            except ValueError:
                return value

    def parse_bracketed(s):
        stack = []
        current = []
        brackets = 0
        temp_str = ''

        for char in s:
            if char == '[':
                if brackets > 0:
                    temp_str += char
                brackets += 1
                if brackets == 1:
                    continue
            elif char == ']':
                brackets -= 1
                if brackets > 0:
                    temp_str += char
                if brackets == 0:
                    current.append(parse_bracketed(temp_str))
                    temp_str = ''
                continue
            elif char == ',' and brackets == 0:
                current.append(convert(temp_str))
                temp_str = ''
            else:
                temp_str += char

        if temp_str:
            current.append(convert(temp_str))
        return current

    if not s:
        return []

    if s.startswith('[') and s.endswith(']'):
        return parse_bracketed(s[1:-1])

    return [convert(val) for val in s.split(',')]

