def add_item(inventory):
    """
    Adds an item to the inventory or updates the quantity if the item already exists.

    Args:
        inventory (dict): The inventory dictionary where items and their quantities are stored.
    """
    name = input("Enter the name of the item to add: ").strip()
    quantity = int(input("Enter the quantity to add: ").strip())
    if name in inventory:
        inventory[name] += quantity
    else:
        inventory[name] = quantity
    print(f"Added {quantity} of {name} to the inventory.")

def remove_item(inventory):
    """
    Removes a specified quantity of an item from the inventory.

    Args:
        inventory (dict): The inventory dictionary where items and their quantities are stored.
    """
    name = input("Enter the name of the item to remove: ").strip()
    if name in inventory:
        quantity = int(input(f"Enter the quantity of {name} to remove: ").strip())
        if inventory[name] > quantity:
            inventory[name] -= quantity
            print(f"Removed {quantity} of {name} from the inventory.")
        elif inventory[name] == quantity:
            del inventory[name]
            print(f"All of {name} have been removed from the inventory.")
        else:
