def text_editor():
    """
    Simulates a simple text editor that supports adding, inserting, deleting, and displaying lines of text.
    """
    document = []

    print("Welcome to the simple text editor!")
    print("Supported commands: ADD, INSERT, DELETE, DISPLAY.")
    print("Format: <COMMAND> [INDEX]: <TEXT> (INDEX is required for INSERT and DELETE)")

    while True:
        command = input("Enter command: ").strip()

        if command.startswith("ADD:"):
            line = command[4:].strip()
            document.append(line)
            print("Line added.")

        elif command.startswith("INSERT "):
            try:
                colon_index = command.find(':')
                index = int(command[7:colon_index-1])
                line = command[colon_index + 1:].strip()

                if 0 <= index <= len(document):
                    document.insert(index, line)
                    print(f"Line inserted at position {index}.")
                else:
                    print("Invalid index.")
            except ValueError:
                print("Invalid command format.")

        elif command.startswith("DELETE "):
            try:
                index = int(command[7:].strip())

                if 0 <= index < len(document):
                    del document[index]
                    print(f"Line deleted from position {index}.")
                else:
                    print("Invalid index.")
            except ValueError:
                print("Invalid command format.")

        elif command == "DISPLAY":
            print("Current document:")
            for i, line in enumerate(document):
                print(f"{i}: {line}")

        elif command == "EXIT":
            print("Exiting the text editor.")
            break

        else:
            print("Invalid command.")

if __name__ == "__main__":
    text_editor()

import subprocess

def check_servers(server_ip_list, timeout=1):
    """
    Check which servers in the list are down.
    A server is considered 'down' if it does not respond to a ping request within the timeout period.

    :param server_ip_list: List of server IP addresses.
    :param timeout: Timeout for ping command in seconds.
    :return: List of IP addresses for servers that are down.
    """
    down_servers = []

    for ip in server_ip_list:
        try:
            response = subprocess.run(['ping', '-c', '1', '-W', str(timeout), ip],
                                    stdout=subprocess.PIPE, stderr=subprocess.PIPE)
            if response.returncode != 0:
                down_servers.append(ip)
        except Exception as e:
            print(f"An error occurred while checking {ip}: {e}")
            down_servers.append(ip)

    return down_servers

def main():
    n = int(input().strip())
    server_ips = input().strip().split()
    timeout = int(input().strip())

    down_servers = check_servers(server_ips, timeout)

    if down_servers:
        for ip in down_servers:
            print(ip)
    else:
        print("No down servers")

if __name__ == "__main__":
    main()

def sanitize_input(s):
    """
    Recursively sanitizes the input string by replacing all instances of '\\r' and '\\n' with a single space,
    ensuring no consecutive spaces are present, and encloses the result in square brackets.

    :param s: The input string to be sanitized.
    :return: A sanitized string enclosed in square brackets.
    """
    def sanitize_helper(idx, result):
        if idx >= len(s):
            return result
        char = s[idx]
        if char == '\r' or char == '\n':
            return sanitize_helper(idx + 1, result + ' ' if result and result[-1] != ' ' else result)
        else:
            return sanitize_helper(idx + 1, result + char)

    sanitized_string = sanitize_helper(0, '')
    return '[' + sanitized_string + ']'

def factorial(n):
    """
    Calculate the factorial of a non-negative integer n using recursion.

    Parameters:
    n (int): A non-negative integer whose factorial is to be computed.

    Returns:
    int: The factorial of the integer n.

    Raises:
    ValueError: If n is a negative integer.
    """
    if n < 0:
        raise ValueError("Input must be a non-negative integer")
    elif n == 0 or n == 1:
        return 1
    else:
        return n * factorial(n - 1)

def main():
    """
    Main function to read input, compute factorial, and print the result.
    """
    try:
        user_input = int(input().strip())
        result = factorial(user_input)
        print(result)
    except ValueError as e:
        print(f"Invalid input: {e}")

if __name__ == "__main__":
    main()

