import math
import operator

def parse_expression(expr):
    """
    Parses an infix expression into a list of tokens (numbers and operators).
    """
    tokens = []
    number_buffer = []
    for c in expr:
        if c.isdigit() or c == '.':
            number_buffer.append(c)
        else:
            if number_buffer:
                tokens.append(float(''.join(number_buffer)))
                number_buffer = []
            if c in "+-*/()":
                tokens.append(c)
            elif c in "stn":
                if c == 's':
                    tokens.append(math.sin)
                elif c == 'c':
                    tokens.append(math.cos)
                elif c == 't':
                    tokens.append(math.tan)
    if number_buffer:
        tokens.append(float(''.join(number_buffer)))
    return tokens

def apply_operator(operators, values):
    """
    Applies the top operator from the operators stack to the top values from the values stack.
    """
    operator = operators.pop()
    right = values.pop()
    left = values.pop()
    if operator == '+':
        values.append(left + right)
    elif operator == '-':
        values.append(left - right)
    elif operator == '*':
        values.append(left * right)
    elif operator == '/':
        values.append(left / right)
    elif is_trigonometric_function(operator):
        values.append(operator(right))
    else:
        raise ValueError("Invalid operator!")

def greater_precedence(op1, op2):
    """
    Returns True if op1 has greater precedence than op2.
    """
    precedences = {'+': 1, '-': 1, '*': 2, '/': 2, '(': 0}
    return precedences[op1] > precedences[op2]

def is_trigonometric_function(op):
    """
    Returns True if op is a trigonometric function.
    """
    return op in (math.sin, math.cos, math.tan)

def evaluate_infix(tokens):
    """
    Evaluates an infix expression represented as a list of tokens.
    """
    operators = []
    values = []
    for token in tokens:
        if isinstance(token, float):
            values.append(token)
        elif token == '(':
            operators.append(token)
        elif token == ')':
            while operators and operators[-1] != '(':
                apply_operator(operators, values)
            operators.pop()
        else:
            while (operators and operators[-1] != '(' and 
                   greater_precedence(operators[-1], token)):
                apply_operator(operators, values)
            operators.append(token)
            if token in (math.sin, math.cos, math.tan):
                apply_operator(operators, values)
    while operators:
        apply_operator(operators, values)
    return values[0]

def main():
    """
    Main function to run the command-line calculator.
    """
    print("Simple Command-Line Calculator")
    print("Use sin, cos, tan for trigonometric functions")
    print("Use pi and e for constants")
    print("Enter an expression to evaluate:")
    while True:
        try:
            user_input = input("> ")
            if user_input.lower() == 'exit':
                break
            user_input = user_input.replace(" ", "").replace("sin", "s").replace("cos", "c").replace("tan", "t").replace("pi", str(math.pi)).replace("e", str(math.e))
            tokens = parse_expression(user_input)
            result = evaluate_infix(tokens)
            print(result)
        except ZeroDivisionError:
            print("Math Error: Division by zero.")
        except SyntaxError:
            print("Syntax Error: Invalid expression.")
        except Exception as e:
            print(e)

if __name__ == "__main__":
    main()

