class HashTable:
    def __init__(self, size=100):
        """Initialize a hash table with 'size' buckets, each bucket storing a list for separate chaining."""
        self.size = size
        self.table = [[] for _ in range(self.size)]

    def _hash_function(self, key):
        """Simple hash function to compute the index for a given key."""
        return hash(key) % self.size

    def insert(self, key, value):
        """Insert a key-value pair into the hash table."""
        index = self._hash_function(key)
        for pair in self.table[index]:
            if pair[0] == key:
                pair[1] = value
                return
        self.table[index].append([key, value])

    def delete(self, key):
        """Delete the key-value pair from the hash table if it exists."""
        index = self._hash_function(key)
        for i, pair in enumerate(self.table[index]):
            if pair[0] == key:
                del self.table[index][i]
                return

    def lookup(self, key):
        """Lookup the key in the hash table and return the corresponding value if found."""
        index = self._hash_function(key)
        for pair in self.table[index]:
            if pair[0] == key:
                return pair[1]
        return None

