class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

    def to_dict(self):
        """Converts TreeNode to a nested dictionary."""
        if not self:
            return {}
        left_val = self.left.val if self.left else ''
        right_val = self.right.val if self.right else ''
        return {self.val: {
            left_val: self.left.to_dict() if self.left else {},
            right_val: self.right.to_dict() if self.right else {}
        }}

def sorted_list_to_bst(nums):
    """Converts a sorted list of integers into a balanced BST."""
    if not nums:
        return None

    mid = len(nums) // 2
    root = TreeNode(nums[mid])
    root.left = sorted_list_to_bst(nums[:mid])
    root.right = sorted_list_to_bst(nums[mid+1:])

    return root

