import os

def list_files(directory, exclude_pattern='*.tmp'):
    """
    Recursively lists files in a directory, excluding those that match the exclude pattern.

    :param directory: The directory to recursively list files from.
    :param exclude_pattern: The pattern of filenames to exclude.
    :return: A list of file paths excluding those that match the exclude pattern.
    """
    file_list = []
    for root, _, files in os.walk(directory):
        for file in files:
            if not file.endswith(exclude_pattern[2:]):
                file_list.append(os.path.join(root, file))
    return file_list

