import re
from collections import defaultdict

def read_file(file_path):
    """Reads the content of a file and returns it as a string."""
    with open(file_path, 'r', encoding='utf-8') as file:
        return file.read()

def clean_text(text):
    """Cleans the text by removing non-alphanumeric characters and converting it to lowercase."""
    return re.sub(r'\W+', ' ', text).lower()

def build_markov_chain(text):
    """Builds a Markov chain from the input text."""
    markov_chain = defaultdict(list)
    words = text.split()
    for i in range(len(words) - 1):
        markov_chain[words[i]].append(words[i + 1])
    return markov_chain

def generate_sentence(chain, length):
    """Generates a random sentence of specified length from the Markov chain."""
    word = random.choice(list(chain.keys()))
    sentence = [word]
    for _ in range(length - 1):
        next_words = chain.get(word)
        if not next_words:
            word = random.choice(list(chain.keys()))
        else:
            word = random.choice(next_words)
        sentence.append(word)
    return ' '.join(sentence)

# Main execution
