import random

def get_computer_choice():
    """Randomly select rock, paper, or scissors for the computer."""
    return random.choice(['rock', 'paper', 'scissors'])

def get_player_choice():
    """Prompt the player to enter a choice and validate it."""
    valid_choices = ['rock', 'paper', 'scissors']
    while True:
        player_choice = input("Enter your choice (rock, paper, scissors): ").strip().lower()
        if player_choice in valid_choices:
            return player_choice
        else:
            print("Invalid choice. Please try again.")

def determine_winner(player_choice, computer_choice):
    """Determine the winner of a single round."""
    if player_choice == computer_choice:
        return "tie"
    elif (player_choice == 'rock' and computer_choice == 'scissors') or \
         (player_choice == 'scissors' and computer_choice == 'paper') or \
         (player_choice == 'paper' and computer_choice == 'rock'):
        return "player"
    else:
        return "computer"

def play_round(round_number, player_choice):
    """Play a single round of rock-paper-scissors."""
    computer_choice = get_computer_choice()
    print(f"\nRound {round_number}:")
    print(f"Player chose: {player_choice}")
    print(f"Computer chose: {computer_choice}")

    winner = determine_winner(player_choice, computer_choice)
    if winner == "player":
        print("You win this round!")
        return 1
    elif winner == "computer":
        print("Computer wins this round!")
        return -1
    else:
        print("It's a tie!")
        return 0

def play_game(num_rounds):
    """Play the full game for the specified number of rounds."""
    player_score = 0
    computer_score = 0

    for round_num in range(1, num_rounds + 1):
        player_choice = get_player_choice()
        result = play_round(round_num, player_choice)
        if result == 1:
            player_score += 1
        elif result == -1:
            computer_score += 1

    print("\nFinal Results:")
    print(f"Player Score: {player_score}")
    print(f"Computer Score: {computer_score}")

    if player_score > computer_score:
        print("Congratulations! You won the game!")
    elif player_score < computer_score:
        print("The computer wins the game. Better luck next time!")
    else:
        print("The game is a tie!")

