import random

def partition(arr, low, high):
    """
    Partitions the array around a pivot element and returns the index of the pivot.
    """
    pivot = arr[high]
    i = low - 1
    for j in range(low, high):
        if arr[j] <= pivot:
            i += 1
            arr[i], arr[j] = arr[j], arr[i]
    arr[i + 1], arr[high] = arr[high], arr[i + 1]
    return i + 1

def quickselect(arr, low, high, k):
    """
    Selects the k-th smallest element in the array using the Quickselect algorithm.
    """
    if low == high:
        return arr[low]

    pivot_index = partition(arr, low, high)

    if k == pivot_index:
        return arr[k]
    elif k < pivot_index:
        return quickselect(arr, low, pivot_index - 1, k)
    else:
        return quickselect(arr, pivot_index + 1, high, k)

def find_kth_smallest(arr, k):
    """
    Finds the k-th smallest element in the array, where k is 1-based.
    """
    return quickselect(arr, 0, len(arr) - 1, k - 1)

