from typing import Any

def validate_input(obj: Any, allow_digit_string: bool = False) -> bool:
    """
    Validates if the input object is a valid integer or a digit string based on the allow_digit_string flag.

    Parameters:
    obj (Any): The object to validate.
    allow_digit_string (bool): Whether to consider digit strings as valid integers.

    Returns:
    bool: True if the object is a valid integer or a digit string (if allowed), False otherwise.
    """
    if isinstance(obj, (int, float, bool, str, bytes, tuple, frozenset)):
        if isinstance(obj, int):
            return True
        if isinstance(obj, bytes):
            try:
                return obj.isdigit() and allow_digit_string
            except UnicodeDecodeError:
                return False
        if isinstance(obj, str):
            return obj.isdigit() and allow_digit_string
    return False

import textwrap

class Room:
    def __init__(self, description):
        self.description = description
        self.items = []
        self.north = None
        self.south = None
        self.east = None
        self.west = None

    def add_item(self, item):
