class Game:
    def __init__(self):
        self.inventory = []
        self.current_room = self.create_world()

    def create_world(self):
        """Create and connect the rooms in the game world."""
        start_room = Room("You are in the center room. Paths lead north, south, east, and west.")
        north_room = Room("North room: You see a glimmering sword on a pedestal.")
        north_room.add_item("sword")
        south_room = Room("South room: You see an exit to the south.")
        east_room = Room("East room: You smell something strange.")
        west_room = Room("West room: You see an odd painting on the wall.")

        start_room.north = north_room
        start_room.south = south_room
        start_room.east = east_room
        start_room.west = west_room
        north_room.south = start_room
        south_room.north = start_room
        east_room.west = start_room
        west_room.east = start_room

        return start_room

    def show_inventory(self):
        """Display the player's inventory."""
        if self.inventory:
            print("Inventory: " + ", ".join(self.inventory))
        else:
            print("Your inventory is empty.")

    def take_item(self, item):
        """Take an item from the current room and add it to the inventory."""
        if item in self.current_room.items:
            self.inventory.append(item)
            self.current_room.items.remove(item)
            print(f"You picked up the {item}.")
        else:
            print(f"There is no {item} in this room.")

    def move_player(self, direction):
        """Move the player to the specified direction if possible."""
        next_room = None
        if direction == 'north' and self.current_room.north is not None:
            next_room = self.current_room.north
        elif direction == 'south' and self.current_room.south is not None:
            next_room = self.current_room.south
        elif direction == 'east' and self.current_room.east is not None:
            next_room = self.current_room.east
        elif direction == 'west' and self.current_room.west is not None:
            next_room = self.current_room.west
        else:
            print("You can't go that way!")

        if next_room:
            self.current_room = next_room

    def look(self):
        """Display the current room's description and any items present."""
        print("\n" + textwrap.fill(self.current_room.description, 50))
        if self.current_room.items:
            print("You see the following items: " + ", ".join(self.current_room.items))

    def play(self):
        """Start the game loop, handling user input and game mechanics."""
        print("Welcome to the adventure game!")
        while True:
            self.look()
            command = input("\nWhat would you like to do? ").strip().lower()
            if command in ['north', 'south', 'east', 'west']:
                self.move_player(command)
            elif command == 'look':
                self.look()
            elif command.startswith('take '):
                item_name = command.split()[1]
                self.take_item(item_name)
            elif command == 'inventory' or command == 'check inventory':
                self.show_inventory()
            elif command == 'quit':
                print("Thanks for playing!")
                break
            else:
                print("I don't understand that command.")

# Run the game
